package test;

public class Module {
	private String name;
	private int hp;

	public Module() {
		// TODO Auto-generated constructor stub
	}

	public Module(String name, int hp) {
		super();
		this.name = name;
		this.hp = hp;
	}

	public int getHp() {
		return hp;
	}

	public void setHp(int hp) {
		this.hp = hp;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}

}
